import React from 'react';
import { useState, useEffect } from "react";

import { Container, Row, Col } from 'react-bootstrap';

const Banner = () => {
  const [text, setText] = useState(''); // Initialize text state
  const toRotate = ['Web Developer', 'Web Designer', 'UI/UX Designer'];
  const period = 2000;

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, period);

    return () => {
      clearInterval(ticker);
    };
  }, [text]);

  const tick = () => {
    let i = toRotate.indexOf(text) + 1;
    let nextText = toRotate[i % toRotate.length];
    setText(nextText);
  };

  return (
    <section className="banner" id="home">
      <Container>
        <Row className="align-items-center">
          <Col xs={12} md={6} xl={7}>
            <span className="tagline">Welcome to my Portfolio</span>
            <h1>{`Hi! I'm Judy`} <span className="wrap">{text}</span></h1>

          {/* About Me button */}
          <button className = "aboutMeButton" color="" >
            Learn more about me!
          </button>
        </Col>
      </Row>
	</Container>
	</section>
  );
}

export default Banner;
